--------------------------------------------------------
--  DDL for Table CASHIERS
--------------------------------------------------------

CREATE TABLE "IGTP"."CASHIERS"  (	
	"ID" 				NUMBER(8,0) GENERATED BY DEFAULT AS IDENTITY 
									MINVALUE 1 
									MAXVALUE 9999999999999999999999999999 
									INCREMENT BY 1 
									START WITH 1 
									CACHE 20 
									NOORDER NOCYCLE NOKEEP NOSCALE , 
	"CASHIER_CO" 		VARCHAR2(10 BYTE), 
	"DESCRIPTION" 		VARCHAR2(80 BYTE), 
	"LAST_MOVEMENT_ID" 	NUMBER(8,0) DEFAULT '0', 
	"SERIE" 			VARCHAR2(10 BYTE), 
	"LAST_TICKET_ID" 	NUMBER(8,0) DEFAULT '0', 
	"CASHIER_CLOSE_ID" 	NUMBER(8,0) DEFAULT '0', 
	"K_TYPE_CASH" 		CHAR(1 BYTE) DEFAULT 'F', 
	"SLUG" 				VARCHAR2(60 BYTE), 
	"UUID" 				VARCHAR2(60 BYTE), 
	"USER_ID" 			NUMBER(8,0), 
	"CREATED_AT" 		DATE DEFAULT sysdate, 
	"UPDATE_AT" 		DATE DEFAULT sysdate
) 
SEGMENT CREATION IMMEDIATE 
PCTFREE 10 
PCTUSED 0 
INITRANS 1 
MAXTRANS 255 
NOCOMPRESS LOGGING
STORAGE(
	INITIAL 65536 
	NEXT 1048576 
	MINEXTENTS 1 
	MAXEXTENTS 2147483645
	PCTINCREASE 0 
	FREELISTS 1 
	FREELIST GROUPS 1
	BUFFER_POOL DEFAULT 
	FLASH_CACHE DEFAULT 
	CELL_FLASH_CACHE DEFAULT
)
TABLESPACE "TS_IGTP_DAT" ;
