--------------------------------------------------------
--  DDL for Table ROUTES
--------------------------------------------------------

CREATE TABLE "IGTP"."ROUTES" (	
	"ID" 					NUMBER(8,0) GENERATED BY DEFAULT AS IDENTITY 
                                    	MINVALUE 1 
                                    	MAXVALUE 9999999999999999999999999999 
                                    	INCREMENT BY 1 
                                    	START WITH 1 
                                    	CACHE 20 
                                    	NOORDER NOCYCLE NOKEEP NOSCALE , 
	"ROUTE_CO" 				VARCHAR2(10 BYTE), 
	"DESCRIPTION" 			VARCHAR2(80 BYTE), 
	"FROM_CITY_ID" 			NUMBER(8,0), 
	"TO_CITY_ID" 			NUMBER(8,0), 
	"K_LEVEL_CO" 			CHAR(1 BYTE) DEFAULT 'A', 
	"DISTANCE_KM" 			NUMBER(22,2), 
	"ESTIMATED_TIME_HRS" 	NUMBER(6,2), 
	"SLUG" 					VARCHAR2(60 BYTE), 
	"UUID" 					VARCHAR2(60 BYTE), 
	"USER_ID" 				NUMBER(8,0), 
	"CREATED_AT" 			DATE DEFAULT sysdate, 
	"UPDATED_AT" 			DATE DEFAULT sysdate
) 
SEGMENT CREATION IMMEDIATE 
	PCTFREE 10 
	PCTUSED 0 
	INITRANS 1 
	MAXTRANS 255 
	NOCOMPRESS LOGGING
	STORAGE(
		INITIAL 65536 
		NEXT 1048576 
		MINEXTENTS 1 
		MAXEXTENTS 2147483645
		PCTINCREASE 0 
		FREELISTS 1 
		FREELIST GROUPS 1
		BUFFER_POOL DEFAULT 
		FLASH_CACHE DEFAULT 
		CELL_FLASH_CACHE DEFAULT
	)
TABLESPACE "TS_IGTP_DAT" ;

COMMENT ON TABLE "IGTP"."ROUTES"  IS 'RUTAS LOGISTICAS';
COMMENT ON COLUMN "IGTP"."ROUTES"."ROUTE_CO" IS 'CODIGO DE RUTAS';
COMMENT ON COLUMN "IGTP"."ROUTES"."K_LEVEL_CO" IS 'NIVEL DE DIFICULTAD DE LA RUTA';
