--  DDL for Table table_configurations
--------------------------------------------------------

CREATE TABLE "IGTP"."CONFIGURATIONS" (	
	"ID" 							NUMBER(8,0) GENERATED BY DEFAULT AS IDENTITY 
												MINVALUE 1 
												MAXVALUE 9999999999999999999999999999 
												INCREMENT BY 1 
												START WITH 1 
												CACHE 20 NOORDER  NOCYCLE  NOKEEP  NOSCALE, 
	"LOCAL_CURRENCY_CO" 			VARCHAR2(3 BYTE), 
	"FOREIGN_CURRENCY_CO" 			VARCHAR2(3 BYTE), 
	"LAST_FOREIGN_CURRENCY_Q_VALUE" NUMBER(10,2), 
	"LAST_FOREIGN_CURRENCY_Q_DATE" 	DATE, 
	"COUNTRY_CO" 					VARCHAR2(10 BYTE), 
	"COMPANY_DESCRIPTION" 			VARCHAR2(80 BYTE), 
	"COMPANY_ADDRESS" 				VARCHAR2(250 BYTE), 
	"COMPANY_TELEPHONE_CO" 			VARCHAR2(50 BYTE), 
	"COMPANY_FAX_CO" 				VARCHAR2(50 BYTE), 
	"COMPANY_EMAIL" 				VARCHAR2(60 BYTE), 
	"COMPANY_FISCAL_DOCUMENT_CO" 	VARCHAR2(60 BYTE), 
	"COMPANY_LOGO" 					VARCHAR2(250 BYTE), 
	"DAYS_PER_YEAR" 				NUMBER(3,0) DEFAULT '360', 
	"WEEKS_PER_YEAR" 				NUMBER(2,0) DEFAULT '52', 
	"MONTHS_PER_YEAR" 				NUMBER(2,0) DEFAULT '12', 
	"DAYS_PER_MONTH" 				NUMBER(2,0) DEFAULT '30', 
	"DAYS_PER_WEEK" 				NUMBER(2,0) DEFAULT '7', 
	"HOURS_PER_DAY" 				NUMBER(2,0) DEFAULT '24', 
	"CREATED_AT" 					DATE DEFAULT sysdate, 
	"UPDATED_AT" 					DATE DEFAULT sysdate
) SEGMENT CREATION IMMEDIATE 
	PCTFREE 10 
	PCTUSED 0 
	INITRANS 1 
	MAXTRANS 255 
	NOCOMPRESS LOGGING
	STORAGE(
		INITIAL 65536 
		NEXT 1048576 
		MINEXTENTS 1 
		MAXEXTENTS 2147483645
		PCTINCREASE 0 
		FREELISTS 1 
		FREELIST GROUPS 1
		BUFFER_POOL DEFAULT 
		FLASH_CACHE DEFAULT 
		CELL_FLASH_CACHE DEFAULT
) TABLESPACE "TS_IGTP_DAT" ;
