--  DDL for Table table_trucks
--------------------------------------------------------
CREATE TABLE "IGTP"."TRUCKS" (	
	"ID" 				NUMBER(20,0) GENERATED BY DEFAULT AS IDENTITY 
									MINVALUE 1 
									MAXVALUE 9999999999999999999999999999 
									INCREMENT BY 1 
									START WITH 1 CACHE 20 NOORDER  NOCYCLE  NOKEEP  NOSCALE , 
	"TRUCK_CO" 			VARCHAR2(10 BYTE), 
	"EXTERNAL_CO" 		VARCHAR2(50 BYTE), 
	"TYPE_VEHICLE_ID" 	NUMBER(20,0), 
	"K_TYPE_GAS" 		VARCHAR2(20 BYTE) DEFAULT 'DIESEL', 
	"YEAR" 				NUMBER(4,0), 
	"MODEL" 			VARCHAR2(50 BYTE), 
	"COLOR" 			VARCHAR2(50 BYTE), 
	"SERIAL_ENGINE" 	VARCHAR2(50 BYTE), 
	"SERIAL_CHASSIS" 	VARCHAR2(50 BYTE), 
	"K_STATUS" 			VARCHAR2(20 BYTE) DEFAULT 'AVAILABLE', 
	"PARTNER_ID" 		NUMBER(20,0), 
	"TRANSFER_ID" 		NUMBER(20,0), 
	"EMPLOYEE_ID" 		NUMBER(20,0), 
	"LOCATION_ID" 		NUMBER(20,0), 
	"USER_ID" 			NUMBER(20,0), 
	"CREATED_AT" 		DATE DEFAULT sysdate, 
	"UPDATED_AT" 		DATE DEFAULT sysdate
) SEGMENT CREATION IMMEDIATE 
	PCTFREE 10 
	PCTUSED 0 
	INITRANS 1 
	MAXTRANS 255 
	NOCOMPRESS LOGGING
	STORAGE(
		INITIAL 65536 
		NEXT 1048576 
		MINEXTENTS 1 
		MAXEXTENTS 2147483645
		PCTINCREASE 0 
		FREELISTS 1 
		FREELIST GROUPS 1
		BUFFER_POOL DEFAULT 
		FLASH_CACHE DEFAULT 
		CELL_FLASH_CACHE DEFAULT
) TABLESPACE "TS_IGTP_DAT" ;
